// src/components/Layout.tsx
import React, { useState } from "react";
import { Outlet, Link, useLocation } from "react-router-dom";
import { useColorMode } from "../Context/ThemeContext";
import { IconButton, Tooltip, useTheme } from "@mui/material";
import LightModeIcon from "@mui/icons-material/LightMode";
import DarkModeIcon from "@mui/icons-material/DarkMode";

import {
  AppBar,
  Toolbar,
  Typography,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  Divider,
  Avatar,
} from "@mui/material";
import LogoutIcon from "@mui/icons-material/Logout";
import HomeIcon from "@mui/icons-material/Home";
import InventoryIcon from "@mui/icons-material/Inventory";
import PeopleIcon from "@mui/icons-material/People";
import ReceiptIcon from "@mui/icons-material/Receipt";
import SettingsIcon from "@mui/icons-material/Settings";

const drawerWidth = 80;

const Layout = () => {
  const location = useLocation();
  const isSelected = (path: string) => location.pathname === path;
  const { toggleColorMode } = useColorMode();
  const theme = useTheme();

  return (
    <Box sx={{ display: "flex" }}>
      {/* AppBar */}
      <AppBar
        position="fixed"
        sx={{
          zIndex: (theme) => theme.zIndex.drawer + 1,
          backgroundColor: (theme) =>
            theme.palette.mode === "light"
              ? "#1e3a8a"
              : theme.palette.background.paper,
        }}
      >
        <Toolbar sx={{ display: "flex", justifyContent: "space-between" }}>
          <Typography variant="h6" noWrap>
            Facturación y Control de Ventas
          </Typography>
          <Tooltip title="Cambiar tema">
            <IconButton onClick={toggleColorMode} color="inherit">
              {theme.palette.mode === "dark" ? (
                <LightModeIcon />
              ) : (
                <DarkModeIcon />
              )}
            </IconButton>
          </Tooltip>
        </Toolbar>
      </AppBar>

      {/* Drawer */}
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          "& .MuiDrawer-paper": {
            width: drawerWidth,
            boxSizing: "border-box",
            backgroundColor: (theme) => theme.palette.background.default,
          },
        }}
      >
        <Box sx={{ display: "flex", flexDirection: "column", height: "100%" }}>
          <Typography
            variant="h6"
            sx={{
              p: 2,
              textAlign: "center",
              color: "#1e3a8a",
              fontSize: "1rem",
            }}
          >
            bitepoint
          </Typography>
          <Divider />
          <List>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/inicio"
                sx={{
                  backgroundColor: isSelected("/inicio")
                    ? "rgba(30, 58, 138, 0.2)"
                    : "transparent",
                  justifyContent: "center",
                }}
              >
                <ListItemIcon>
                <HomeIcon sx={{ color: (theme) => theme.palette.primary.main }} />
                </ListItemIcon>
              </ListItemButton>
            </ListItem>
            {/* Agrega los demás ítems igual que arriba */}
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/productos"
                sx={{
                  backgroundColor: isSelected("/productos")
                    ? "rgba(30, 58, 138, 0.2)"
                    : "transparent",
                  justifyContent: "center",
                }}
              >
                <ListItemIcon>
                  <InventoryIcon sx={{ color: (theme) => theme.palette.primary.main }} />
                </ListItemIcon>
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
            <Tooltip title="Clientes" placement="right">
              <ListItemButton
                component={Link}
                to="/clientes"
                sx={{
                  backgroundColor: isSelected("/clientes")
                    ? "rgba(30, 58, 138, 0.2)"
                    : "transparent",
                  justifyContent: "center",
                }}
              >
                <ListItemIcon>
                  <PeopleIcon sx={{ color: (theme) => theme.palette.primary.main }}  />
                </ListItemIcon>
              </ListItemButton>
              </Tooltip>
            </ListItem>
            <ListItem disablePadding>
            <Tooltip title="facturacion" placement="right">
              <ListItemButton
                component={Link}
                to="/facturacion"
                sx={{
                  backgroundColor: isSelected("/facturacion")
                    ? "rgba(30, 58, 138, 0.2)"
                    : "transparent",
                  justifyContent: "center",
                }}
              >
                <ListItemIcon>
                  <ReceiptIcon sx={{ color: (theme) => theme.palette.primary.main }} />
                </ListItemIcon>
              </ListItemButton>
              </Tooltip>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/configuracion"
                sx={{
                  backgroundColor: isSelected("/configuracion")
                    ? "rgba(30, 58, 138, 0.2)"
                    : "transparent",
                  justifyContent: "center",
                }}
              >
                <ListItemIcon>
                  <SettingsIcon sx={{ color: (theme) => theme.palette.primary.main }} />
                </ListItemIcon>
              </ListItemButton>
            </ListItem>
          </List>
          <Divider />
          <Box sx={{ mt: "auto", p: 1 }}>
            <Box sx={{ display: "flex", justifyContent: "center", mb: 1 }}>
              <Avatar
                sx={{
                  backgroundColor: (theme) =>
                    theme.palette.mode === "light"
                      ? "#1e3a8a"
                      : theme.palette.primary.main,
                  width: 40,
                  height: 40,
                  fontSize: "0.8rem",
                  color: (theme) =>
                    theme.palette.getContrastText(theme.palette.primary.main),
                }}
              >
                BT
              </Avatar>
            </Box>
            <ListItem disablePadding>
              <ListItemButton sx={{ justifyContent: "center" }}>
                <ListItemIcon>
                  <LogoutIcon sx={{ color: "#1e3a8a" }} />
                </ListItemIcon>
              </ListItemButton>
            </ListItem>
          </Box>
        </Box>
      </Drawer>

      {/* Aquí se mostrará cada página */}
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          ml: `${drawerWidth}px`, // dejar espacio del menú
        }}
      >
        <Toolbar />
        <Outlet />
      </Box>
    </Box>
  );
};

export default Layout;
