import React, { useState, useEffect, useCallback } from "react";
import EditIcon from "@mui/icons-material/Edit";
import {
  Box,
  Typography,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import DeleteIcon from "@mui/icons-material/Delete";

interface Product {
  descripcion: string;
  cantidad: number;
  precio: number;
  descuento: number; // Descuento en porcentaje
  subtotal: number;
}

interface Totals {
  subtotal: number;
  igv: number;
  total: number;
}

const Facturacion: React.FC = () => {
  const [productos, setProductos] = useState<Product[]>([
    { descripcion: "", cantidad: 1, precio: 0, descuento: 0, subtotal: 0 },
  ]);
  const [totales, setTotales] = useState<Totals>({
    subtotal: 0,
    igv: 0,
    total: 0,
  });
  const [editandoIndex, setEditandoIndex] = useState<number | null>(null);

  const calcularTotales = useCallback((productosActualizados: Product[]) => {
    const subtotal = productosActualizados.reduce(
      (sum, p) => sum + p.subtotal,
      0
    );
    const igv = subtotal * 0.18;
    const total = subtotal + igv;
    setTotales({ subtotal, igv, total });
  }, []);

  useEffect(() => {
    calcularTotales(productos);
  }, [productos, calcularTotales]);

  const handleProductoChange = useCallback(
    (index: number, field: keyof Product, value: string | number) => {
      setProductos((prev) =>
        prev.map((producto, i) => {
          if (i !== index) return producto;

          const updated = {
            ...producto,
            [field]:
              field === "cantidad" ||
              field === "precio" ||
              field === "descuento"
                ? parseFloat(value as string) || 0
                : value,
          };

          const subtotal =
            updated.cantidad *
            updated.precio *
            (1 - (updated.descuento || 0) / 100);

          return {
            ...updated,
            subtotal,
          };
        })
      );
    },
    []
  );

  const agregarProducto = useCallback(() => {
    setProductos((prev) => [
      ...prev,
      { descripcion: "", cantidad: 1, precio: 0, subtotal: 0 },
    ]);
  }, []);

  const eliminarProducto = useCallback((index: number) => {
    setProductos((prev) => prev.filter((_, i) => i !== index));
  }, []);

  const limpiarFactura = useCallback(() => {
    setProductos([{ descripcion: "", cantidad: 1, precio: 0, subtotal: 0 }]);
    setTotales({ subtotal: 0, igv: 0, total: 0 });
  }, []);

  const anularFactura = () => {
    alert("Factura anulada");
  };

  return (
    <Box sx={{ p: 3, backgroundColor: "#f9f9f9", borderRadius: 2 }}>
      <Typography
        variant="h5"
        sx={{ mb: 3, color: "primary.main", fontWeight: "bold" }}
      >
        Facturación Electrónica
      </Typography>

      {/* Información de la factura */}
      <Box
        sx={{
          display: "grid",
          gridTemplateColumns: "repeat(4, 1fr)",
          gap: 2,
          mb: 3,
        }}
      >
        <FormControl fullWidth size="small">
          <InputLabel>Tipo Documento</InputLabel>
          <Select defaultValue="FACTURA" label="Tipo Documento">
            <MenuItem value="FACTURA">Factura</MenuItem>
            <MenuItem value="BOLETA">Boleta</MenuItem>
          </Select>
        </FormControl>
        <TextField label="Serie" size="small" defaultValue="F001" />
        <TextField label="Correlativo" size="small" defaultValue="0005" />
        <FormControl fullWidth size="small">
          <InputLabel>Moneda</InputLabel>
          <Select defaultValue="Soles" label="Moneda">
            <MenuItem value="Soles">Soles</MenuItem>
            <MenuItem value="Dólares">Dólares</MenuItem>
          </Select>
        </FormControl>
        <TextField
          label="Fecha"
          type="date"
          size="small"
          InputLabelProps={{ shrink: true }}
        />
        <TextField
          label="F. Vencimiento"
          type="date"
          size="small"
          InputLabelProps={{ shrink: true }}
        />
      </Box>

      {/* Cliente */}
      <Box
        sx={{ display: "grid", gridTemplateColumns: "3fr 1fr", gap: 2, mb: 3 }}
      >
        <TextField
          label="Cliente"
          size="small"
          placeholder="Buscar cliente por RUC o razón social"
        />
        <Button variant="contained" size="small">
          Registrar Cliente
        </Button>
      </Box>

      {/* Producto */}
      <Box
        sx={{ display: "grid", gridTemplateColumns: "3fr 1fr", gap: 2, mb: 3 }}
      >
        <TextField
          label="Producto"
          size="small"
          placeholder="Buscar producto por código o descripción"
        />
        <Button variant="contained" size="small">
          Item Detallado
        </Button>
      </Box>

      {/* Tabla */}
      <TableContainer component={Paper} sx={{ mb: 3 }}>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>Descripción</TableCell>
              <TableCell align="right">Cantidad</TableCell>
              <TableCell align="right">Precio Unitario (S/.)</TableCell>
              <TableCell align="right">Descuento (%)</TableCell>
              <TableCell align="right">Subtotal (S/.)</TableCell>
              <TableCell align="center">Acciones</TableCell>
            </TableRow>
          </TableHead>

          <TableBody>
  {productos.map((producto, index) => {
    const isEditing = editandoIndex === index;

    return (
      <TableRow key={index}>
        {/* Descripción */}
        <TableCell>
          {isEditing ? (
            <TextField
              fullWidth
              size="small"
              value={descripcionTemp}
              onChange={(e) => setDescripcionTemp(e.target.value)}
            />
          ) : (
            <Typography>{producto.descripcion || <i>Sin descripción</i>}</Typography>
          )}
        </TableCell>

        {/* Cantidad */}
        <TableCell align="right">
          <TextField
            type="number"
            size="small"
            inputProps={{ min: 0 }}
            value={producto.cantidad}
            onChange={(e) =>
              handleProductoChange(index, 'cantidad', e.target.value)
            }
          />
        </TableCell>

        {/* Precio - no editable */}
        <TableCell align="right">
          <TextField
            type="number"
            size="small"
            inputProps={{ min: 0, step: '0.01' }}
            value={producto.precio}
            disabled
          />
        </TableCell>

        {/* Descuento */}
        <TableCell align="right">
          <TextField
            type="number"
            size="small"
            inputProps={{ min: 0, max: 100, step: '0.1' }}
            value={producto.descuento}
            onChange={(e) =>
              handleProductoChange(index, 'descuento', e.target.value)
            }
          />
        </TableCell>

        {/* Subtotal */}
        <TableCell align="right">
          {producto.subtotal.toFixed(2)}
        </TableCell>

        {/* Acciones */}
        <TableCell align="center">
          {isEditing ? (
            <>
              <Button
                size="small"
                color="success"
                onClick={() => {
                  handleProductoChange(index, 'descripcion', descripcionTemp);
                  setEditandoIndex(null);
                }}
              >
                Guardar
              </Button>
              <Button
                size="small"
                onClick={() => setEditandoIndex(null)}
              >
                Cancelar
              </Button>
            </>
          ) : (
            <>
              <IconButton
                color="primary"
                onClick={() => {
                  setEditandoIndex(index);
                  setDescripcionTemp(producto.descripcion);
                }}
              >
                <EditIcon fontSize="small" />
              </IconButton>
              <IconButton
                color="error"
                onClick={() => eliminarProducto(index)}
              >
                <DeleteIcon fontSize="small" />
              </IconButton>
            </>
          )}
        </TableCell>
      </TableRow>
    );
  })}
</TableBody>

        </Table>
      </TableContainer>

      <Button
        variant="outlined"
        size="small"
        startIcon={<AddIcon />}
        onClick={agregarProducto}
        sx={{ mb: 3 }}
      >
        Agregar Producto
      </Button>

      {/* Totales */}
      <Box
        sx={{
          display: "grid",
          gridTemplateColumns: "repeat(3, 1fr)",
          gap: 2,
          mb: 3,
        }}
      >
        <TextField
          label="Subtotal (S/.)"
          size="small"
          value={totales.subtotal.toFixed(2)}
          disabled
        />
        <TextField
          label="IGV (18%)"
          size="small"
          value={totales.igv.toFixed(2)}
          disabled
        />
        <TextField
          label="Total (S/.)"
          size="small"
          value={totales.total.toFixed(2)}
          disabled
        />
      </Box>

      {/* Botones */}
      <Box sx={{ display: "flex", justifyContent: "flex-end", gap: 2 }}>
        <Button variant="contained" size="small">
          Guardar
        </Button>
        <Button
          variant="outlined"
          color="secondary"
          size="small"
          onClick={limpiarFactura}
        >
          Limpiar Factura
        </Button>
        <Button
          variant="outlined"
          color="error"
          size="small"
          onClick={anularFactura}
        >
          Anular Factura
        </Button>
        <Button variant="outlined" color="secondary" size="small">
          Cancelar
        </Button>
      </Box>
    </Box>
  );
};

export default Facturacion;
