// theme.ts
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1E88E5', // azul corporativo
    },
    secondary: {
      main: '#6D7580', // gris neutro
    },
    error: {
      main: '#D32F2F', // rojo alerta
    },
    background: {
      default: '#F4F6F8', // fondo gris claro
      paper: '#ffffff',
    },
    text: {
      primary: '#212121',
      secondary: '#5A5A5A',
    },
  },
  typography: {
    fontFamily: ['"Inter"', 'Roboto', '"Helvetica Neue"', 'Arial', 'sans-serif'].join(','),
    h5: {
      fontWeight: 600,
      letterSpacing: '0.2px',
    },
    button: {
      textTransform: 'none', // evita mayúsculas automáticas
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 6,
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 10,
        },
      },
    },
  },
});

export default theme;
