// src/components/Layout.tsx
import React from 'react';
import { Outlet, Link, useLocation } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Divider,
  Avatar,
} from '@mui/material';
import LogoutIcon from '@mui/icons-material/Logout';
import HomeIcon from '@mui/icons-material/Home';
import InventoryIcon from '@mui/icons-material/Inventory';
import PeopleIcon from '@mui/icons-material/People';
import ReceiptIcon from '@mui/icons-material/Receipt';
import SettingsIcon from '@mui/icons-material/Settings';

const drawerWidth = 240; // Ajustamos el ancho para incluir texto

const Layout = () => {
  const location = useLocation();

  const isSelected = (path: string) => location.pathname === path;

  return (
    <Box sx={{ display: 'flex' }}>
      {/* AppBar */}
      <AppBar
        position="fixed"
        sx={{
          zIndex: (theme) => theme.zIndex.drawer + 1,
          backgroundColor: '#1e3a8a',
        }}
      >
        <Toolbar>
          <Typography variant="h6" noWrap>
            Facturación y Control de Ventas
          </Typography>
        </Toolbar>
      </AppBar>

      {/* Drawer */}
      <Drawer
        variant="permanent"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
            backgroundColor: '#e5e7eb',
          },
        }}
      >
        <Box sx={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
          <Typography variant="h6" sx={{ p: 2, textAlign: 'center', color: '#1e3a8a', fontSize: '1rem' }}>
            bitepoint
          </Typography>
          <Divider />
          <List>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/inicio"
                sx={{
                  backgroundColor: isSelected('/inicio') ? 'rgba(30, 58, 138, 0.2)' : 'transparent',
                }}
              >
                <ListItemIcon>
                  <HomeIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
                <ListItemText primary="Inicio" sx={{ color: '#1e3a8a' }} />
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/productos"
                sx={{
                  backgroundColor: isSelected('/productos') ? 'rgba(30, 58, 138, 0.2)' : 'transparent',
                }}
              >
                <ListItemIcon>
                  <InventoryIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
                <ListItemText primary="Productos" sx={{ color: '#1e3a8a' }} />
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/clientes"
                sx={{
                  backgroundColor: isSelected('/clientes') ? 'rgba(30, 58, 138, 0.2)' : 'transparent',
                }}
              >
                <ListItemIcon>
                  <PeopleIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
                <ListItemText primary="Clientes" sx={{ color: '#1e3a8a' }} />
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/facturacion"
                sx={{
                  backgroundColor: isSelected('/facturacion') ? 'rgba(30, 58, 138, 0.2)' : 'transparent',
                }}
              >
                <ListItemIcon>
                  <ReceiptIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
                <ListItemText primary="Facturación" sx={{ color: '#1e3a8a' }} />
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/configuracion"
                sx={{
                  backgroundColor: isSelected('/configuracion') ? 'rgba(30, 58, 138, 0.2)' : 'transparent',
                }}
              >
                <ListItemIcon>
                  <SettingsIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
                <ListItemText primary="Configuración" sx={{ color: '#1e3a8a' }} />
              </ListItemButton>
            </ListItem>
          </List>
          <Divider />
          <Box sx={{ mt: 'auto', p: 1 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <Avatar sx={{ backgroundColor: '#1e3a8a', width: 40, height: 40, fontSize: '0.8rem' }}>BT</Avatar>
              <Box sx={{ ml: 1 }}>
                <Typography variant="body2" sx={{ color: '#1e3a8a', fontSize: '0.8rem' }}>
                  Boris Travi
                </Typography>
                <Typography variant="caption" color="textSecondary">
                  Administrador
                </Typography>
              </Box>
            </Box>
            <ListItem disablePadding>
              <ListItemButton>
                <ListItemIcon>
                  <LogoutIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
                <ListItemText primary="Logout" sx={{ color: '#1e3a8a' }} />
              </ListItemButton>
            </ListItem>
          </Box>
        </Box>
      </Drawer>

      {/* Aquí se mostrará cada página */}
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          ml: `${drawerWidth}px`, // dejar espacio del menú
        }}
      >
        <Toolbar />
        <Outlet />
      </Box>
    </Box>
  );
};

export default Layout;
