// src/components/Facturacion.tsx
import React, { useState, useEffect, useCallback } from 'react';
import { useTheme } from '@mui/material/styles';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  IconButton,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
} from '@mui/material';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import DeleteIcon from '@mui/icons-material/Delete';
import { DataGrid, GridColDef, GridCellEditStopParams } from '@mui/x-data-grid';
import logo from '../assets/logo.png'; 

interface Product {
  id: number;
  numeroParte: string;
  descripcion: string;
  cantidad: number;
  precio: number;
  descuento: number;
  subtotal: number;
}

interface Totals {
  subtotal: number;
  igv: number;
  total: number;
}

const Facturacion: React.FC = () => {
  const theme = useTheme();
  const isDark = theme.palette.mode === 'dark';

  const listaPrecios = [
    { codigo: 'P001', numeroParte: 'NP001', descripcion: 'Producto 1', precio: 10.0 },
    { codigo: 'P002', numeroParte: 'NP002', descripcion: 'Producto 2', precio: 20.0 },
    { codigo: 'P003', numeroParte: 'NP003', descripcion: 'Producto 3', precio: 30.0 },
  ];

  const [productos, setProductos] = useState<Product[]>([]);
  const [totales, setTotales] = useState<Totals>({ subtotal: 0, igv: 0, total: 0 });
  const [productoSeleccionado, setProductoSeleccionado] = useState<string>("");

  const calcularTotales = useCallback((prods: Product[]) => {
    const subtotal = prods.reduce((sum, p) => sum + (p.subtotal || 0), 0);
    const igv = subtotal * 0.18;
    const total = subtotal + igv;
    setTotales({ subtotal, igv, total });
  }, []);

  useEffect(() => {
    calcularTotales(productos);
  }, [productos, calcularTotales]);

  const agregarProducto = () => {
    const producto = listaPrecios.find((p) => p.codigo === productoSeleccionado);
    if (producto) {
      setProductos((prev) => [
        ...prev,
        {
          id: prev.length + 1,
          numeroParte: producto.numeroParte,
          descripcion: producto.descripcion,
          cantidad: 1,
          precio: producto.precio,
          descuento: 0,
          subtotal: producto.precio,
        },
      ]);
    }
  };

  const eliminarProducto = (id: number) => {
    if (window.confirm("¿Estás seguro de que deseas eliminar este producto?")) {
      setProductos((prev) => prev.filter((producto) => producto.id !== id));
    }
  };

  const handleProductoChange = useCallback(
    (id: number, field: keyof Product, value: string | number) => {
      setProductos((prev) =>
        prev.map((producto) => {
          if (producto.id !== id) return producto;

          const updated = {
            ...producto,
            [field]:
              field === "cantidad" || field === "descuento"
                ? Math.max(0, parseFloat(value as string) || 0)
                : value,
          };

          updated.subtotal =
            updated.cantidad *
            updated.precio *
            (1 - (updated.descuento || 0) / 100);

          return updated;
        })
      );
    },
    []
  );

  const columns: GridColDef[] = [
    { field: 'numeroParte', headerName: 'Número de Parte', width: 140 },
    { field: 'descripcion', headerName: 'Descripción', width: 200, editable: true },
    { field: 'cantidad', headerName: 'Cantidad', type: 'number', width: 100, editable: true },
    { field: 'precio', headerName: 'Precio Unitario (S/.)', type: 'number', width: 160 },
    { field: 'descuento', headerName: 'Descuento (%)', type: 'number', width: 130, editable: true },
    {
      field: 'subtotal',
      headerName: 'Subtotal (S/.)',
      type: 'number',
      width: 160,
      valueFormatter: ({ value }) => {
        const subtotal = value || 0;
        return subtotal.toFixed(2);
      },
    },
    {
      field: 'actions',
      headerName: 'Acciones',
      width: 100,
      sortable: false,
      filterable: false,
      renderCell: (params) => (
        <IconButton
          size="small"
          color="error"
          onClick={() => eliminarProducto(params.id as number)}
        >
          <DeleteIcon fontSize="small" />
        </IconButton>
      ),
    },
  ];

  const rows = productos.map((p) => ({
    ...p,
    subtotal: p.subtotal || 0,
  }));

  return (
    <Box
      sx={{
        p: 3,
        backgroundColor: 'background.paper',
        borderRadius: 2,
        boxShadow: isDark ? 1 : 2,
      }}
    >
      {/* Header */}
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <img src={logo} alt="Logo" style={{ height: 40, marginRight: 16 }} />
        <Typography variant="h5" sx={{ fontWeight: 'bold', color: '#1e3a8a' }}>
          Facturación Electrónica
        </Typography>
      </Box>

      {/* Información de la factura */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 2 }}>
          Información de la Factura
        </Typography>
        <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: 2 }}>
          <FormControl fullWidth size="small">
            <InputLabel>Tipo Documento</InputLabel>
            <Select defaultValue="FACTURA" label="Tipo Documento">
              <MenuItem value="FACTURA">Factura</MenuItem>
              <MenuItem value="BOLETA">Boleta</MenuItem>
            </Select>
          </FormControl>
          <TextField label="Serie" size="small" defaultValue="F001" />
          <TextField label="Correlativo" size="small" defaultValue="0005" />
          <FormControl fullWidth size="small">
            <InputLabel>Moneda</InputLabel>
            <Select defaultValue="Soles" label="Moneda">
              <MenuItem value="Soles">Soles</MenuItem>
              <MenuItem value="Dólares">Dólares</MenuItem>
            </Select>
          </FormControl>
          <TextField
            label="Fecha"
            type="date"
            size="small"
            InputLabelProps={{ shrink: true }}
          />
          <TextField
            label="F. Vencimiento"
            type="date"
            size="small"
            InputLabelProps={{ shrink: true }}
          />
        </Box>
      </Paper>

      {/* Cliente */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 2 }}>
          Información del Cliente
        </Typography>
        <Box sx={{ display: 'grid', gridTemplateColumns: '3fr 1fr', gap: 2 }}>
          <TextField
            label="Cliente"
            size="small"
            placeholder="Buscar cliente por RUC o razón social"
          />
          <Button
            variant="contained"
            size="small"
            startIcon={<PersonAddIcon />}
            sx={{
              backgroundColor: '#1e3a8a',
              '&:hover': { backgroundColor: '#163a6a' },
            }}
          >
            Registrar Cliente
          </Button>
        </Box>
      </Paper>

      {/* Productos */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 2 }}>
          Productos
        </Typography>
        <Box
          sx={{
            display: 'grid',
            gridTemplateColumns: '3fr 1fr',
            gap: 2,
            mb: 3,
          }}
        >
          <FormControl fullWidth size="small">
            <InputLabel>Producto</InputLabel>
            <Select
              value={productoSeleccionado}
              onChange={(e) => setProductoSeleccionado(e.target.value)}
              label="Producto"
            >
              {listaPrecios.map((producto) => (
                <MenuItem key={producto.codigo} value={producto.codigo}>
                  {producto.descripcion}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <Button
            variant="contained"
            size="small"
            startIcon={<AddShoppingCartIcon />}
            onClick={agregarProducto}
            sx={{
              backgroundColor: '#1e3a8a',
              '&:hover': { backgroundColor: '#163a6a' },
            }}
          >
            Agregar Producto
          </Button>
        </Box>

        <Paper sx={{ height: 400, width: '100%' }}>
          <DataGrid
            rows={rows}
            columns={columns}
            initialState={{
              pagination: {
                paginationModel: { pageSize: 5 },
              },
            }}
            paginationModel={{ page: 0, pageSize: 5 }}
            disableRowSelectionOnClick
            onCellEditStop={(params: GridCellEditStopParams) => {
              handleProductoChange(
                Number(params.id),
                params.field as keyof Product,
                params.value as string | number
              );
            }}
          />
        </Paper>
      </Paper>

      {/* Resumen Financiero */}
      <Paper elevation={2} sx={{ p: 3, mb: 3 }}>
        <Typography variant="subtitle1" sx={{ fontWeight: 'bold', mb: 2 }}>
          Resumen Financiero
        </Typography>
        <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: 2 }}>
          <TextField
            label="Subtotal (S/.)"
            size="small"
            value={totales.subtotal.toFixed(2)}
            disabled
          />
          <TextField
            label="IGV (18%)"
            size="small"
            value={totales.igv.toFixed(2)}
            disabled
          />
          <TextField
            label="Total (S/.)"
            size="small"
            value={totales.total.toFixed(2)}
            disabled
          />
        </Box>
      </Paper>

      {/* Botones finales */}
      <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 2 }}>
        <Button
          variant="contained"
          size="small"
          sx={{ backgroundColor: '#1e3a8a', '&:hover': { backgroundColor: '#163a6a' } }}
        >
          Guardar
        </Button>
        <Button variant="outlined" color="secondary" size="small" onClick={limpiarFactura}>
          Limpiar Factura
        </Button>
        <Button variant="outlined" color="error" size="small" onClick={anularFactura}>
          Anular Factura
        </Button>
        <Button variant="outlined" color="secondary" size="small">
          Cancelar
        </Button>
      </Box>
    </Box>
  );
};

export default Facturacion;
