import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';

// Definimos interfaces para tipar los productos y totales
interface Product {
  descripcion: string;
  cantidad: number;
  precio: number;
  subtotal: number;
}

interface Totals {
  subtotal: number;
  igv: number;
  total: number;
}

const Facturacion: React.FC = () => {
  const [productos, setProductos] = useState<Product[]>([
    { descripcion: '', cantidad: 1, precio: 0, subtotal: 0 },
  ]);
  const [totales, setTotales] = useState<Totals>({ subtotal: 0, igv: 0, total: 0 });

  // Se recalculan los totales cada vez que cambian los productos
  useEffect(() => {
    const newSubtotal = productos.reduce((acc, prod) => acc + prod.subtotal, 0);
    const newIgv = newSubtotal * 0.18;
    const newTotal = newSubtotal + newIgv;
    setTotales({ subtotal: newSubtotal, igv: newIgv, total: newTotal });
  }, [productos]);

  const handleProductoChange = (
    index: number,
    field: keyof Product,
    value: string
  ) => {
    const updatedProductos = [...productos];
    const productoActual = updatedProductos[index];

    // Se actualiza el valor segÃºn el campo
    if (field === 'cantidad' || field === 'precio') {
      const numericValue = parseFloat(value) || 0;
      productoActual[field] = numericValue;
    } else if (field === 'descripcion') {
      productoActual[field] = value;
    }

    // Se recalcula el subtotal del producto
    productoActual.subtotal = productoActual.cantidad * productoActual.precio;
    setProductos(updatedProductos);
  };

  const agregarProducto = () => {
    setProductos((prev) => [
      ...prev,
      { descripcion: '', cantidad: 1, precio: 0, subtotal: 0 },
    ]);
  };

  const eliminarProducto = (index: number) => {
    const updatedProductos = productos.filter((_, i) => i !== index);
    setProductos(updatedProductos);
  };

  return (
    <Box sx={{ p: 2 }}>
      {/* Encabezado */}
      <Typography variant="h5" sx={{ mb: 2, color: 'primary.main' }}>
        FacturaciÃ³n ElectrÃ³nica
      </Typography>

      {/* InformaciÃ³n de la factura */}
      <Box sx={{ display: 'flex', gap: 2, mb: 2, flexWrap: 'wrap' }}>
        <TextField label="RUC" variant="outlined" size="small" fullWidth required />
        <TextField label="NÂ° Factura" variant="outlined" size="small" fullWidth required />
        <TextField
          label="Fecha"
          type="date"
          variant="outlined"
          size="small"
          fullWidth
          InputLabelProps={{ shrink: true }}
          required
        />
      </Box>

      {/* InformaciÃ³n del cliente */}
      <Box sx={{ display: 'flex', gap: 2, mb: 2, flexWrap: 'wrap' }}>
        <TextField label="Cliente" variant="outlined" size="small" fullWidth required />
        <TextField label="RUC/DNI" variant="outlined" size="small" fullWidth required />
        <TextField label="DirecciÃ³n" variant="outlined" size="small" fullWidth />
      </Box>

      {/* Detalle de productos/servicios */}
      <TableContainer component={Paper} sx={{ mb: 2 }}>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>DescripciÃ³n</TableCell>
              <TableCell align="right">Cantidad</TableCell>
              <TableCell align="right">Precio Unitario (S/.)</TableCell>
              <TableCell align="right">Subtotal (S/.)</TableCell>
              <TableCell align="center">Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {productos.map((producto, index) => (
              <TableRow key={index}>
                <TableCell>
                  <TextField
                    variant="outlined"
                    size="small"
                    fullWidth
                    value={producto.descripcion}
                    onChange={(e) =>
                      handleProductoChange(index, 'descripcion', e.target.value)
                    }
                  />
                </TableCell>
                <TableCell align="right">
                  <TextField
                    type="number"
                    variant="outlined"
                    size="small"
                    value={producto.cantidad}
                    onChange={(e) =>
                      handleProductoChange(index, 'cantidad', e.target.value)
                    }
                    inputProps={{ min: 0 }}
                  />
                </TableCell>
                <TableCell align="right">
                  <TextField
                    type="number"
                    variant="outlined"
                    size="small"
                    value={producto.precio}
                    onChange={(e) =>
                      handleProductoChange(index, 'precio', e.target.value)
                    }
                    inputProps={{ min: 0 }}
                  />
                </TableCell>
                <TableCell align="right">{producto.subtotal.toFixed(2)}</TableCell>
                <TableCell align="center">
                  <IconButton color="error" onClick={() => eliminarProducto(index)}>
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Button
        variant="outlined"
        size="small"
        startIcon={<AddIcon />}
        onClick={agregarProducto}
        sx={{ mb: 2 }}
      >
        Agregar Producto
      </Button>

      {/* Totales */}
      <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 2, mt: 2, flexWrap: 'wrap' }}>
        <TextField
          label="Subtotal (S/.)"
          variant="outlined"
          size="small"
          value={totales.subtotal.toFixed(2)}
          disabled
        />
        <TextField
          label="IGV (18%)"
          variant="outlined"
          size="small"
          value={totales.igv.toFixed(2)}
          disabled
        />
        <TextField
          label="Total (S/.)"
          variant="outlined"
          size="small"
          value={totales.total.toFixed(2)}
          disabled
        />
      </Box>

      {/* Botones de acciÃ³n */}
      <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 2, mt: 2 }}>
        <Button variant="contained" color="primary" size="small">
          Guardar
        </Button>
        <Button variant="outlined" color="secondary" size="small">
          Cancelar
        </Button>
      </Box>
    </Box>
  );
};

export default Facturacion;
