import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Paper,
  Typography,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';

// Definimos interfaces para tipar los productos y totales
interface Product {
  descripcion: string;
  cantidad: number;
  precio: number;
  subtotal: number;
}

interface Totals {
  subtotal: number;
  igv: number;
  total: number;
}

const Facturacion: React.FC = () => {
  const [productos, setProductos] = useState<Product[]>([
    { descripcion: '', cantidad: 1, precio: 0, subtotal: 0 },
  ]);
  const [totales, setTotales] = useState<Totals>({ subtotal: 0, igv: 0, total: 0 });

  // Se recalculan los totales cada vez que cambian los productos
  useEffect(() => {
    const newSubtotal = productos.reduce((acc, prod) => acc + prod.subtotal, 0);
    const newIgv = newSubtotal * 0.18;
    const newTotal = newSubtotal + newIgv;
    setTotales({ subtotal: newSubtotal, igv: newIgv, total: newTotal });
  }, [productos]);

  const handleProductoChange = (index: number, field: keyof Product, value: string) => {
    const updatedProductos = [...productos];
    const productoActual = updatedProductos[index];

    // Se actualiza el valor según el campo
    if (field === 'cantidad' || field === 'precio') {
      const numericValue = parseFloat(value) || 0;
      productoActual[field] = numericValue;
    } else if (field === 'descripcion') {
      productoActual[field] = value;
    }

    // Se recalcula el subtotal del producto
    productoActual.subtotal = productoActual.cantidad * productoActual.precio;
    setProductos(updatedProductos);
  };

  const agregarProducto = () => {
    setProductos((prev) => [
      ...prev,
      { descripcion: '', cantidad: 1, precio: 0, subtotal: 0 },
    ]);
  };

  const eliminarProducto = (index: number) => {
    const updatedProductos = productos.filter((_, i) => i !== index);
    setProductos(updatedProductos);
  };

  return (
    <Box sx={{ p: 2 }}>
      {/* Título principal */}
      <Typography variant="h6" sx={{ mb: 3, fontWeight: 'bold' }}>
        Comprobantes / Factura
      </Typography>

      <Paper variant="outlined" sx={{ p: 2, mb: 3 }}>
        {/* Primera fila: Tipo de comprobante, serie, correlativo, moneda, fecha, f. vencimiento */}
        <Grid container spacing={2} sx={{ mb: 2 }}>
          <Grid item xs={12} sm={4} md={2}>
            <TextField
              fullWidth
              size="small"
              label="Tipo Comprobante"
              value="FACTURA"
              InputProps={{ readOnly: true }}
            />
          </Grid>
          <Grid item xs={12} sm={4} md={1}>
            <TextField
              fullWidth
              size="small"
              label="Serie"
              value="F001"
              InputProps={{ readOnly: true }}
            />
          </Grid>
          <Grid item xs={12} sm={4} md={1}>
            <TextField
              fullWidth
              size="small"
              label="Correlativo"
              value="0005"
              InputProps={{ readOnly: true }}
            />
          </Grid>
          <Grid item xs={12} sm={4} md={2}>
            <TextField
              fullWidth
              size="small"
              label="Moneda"
              value="Soles"
              InputProps={{ readOnly: true }}
            />
          </Grid>
          <Grid item xs={12} sm={4} md={3}>
            <TextField
              fullWidth
              size="small"
              type="date"
              label="Fecha"
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
          <Grid item xs={12} sm={4} md={3}>
            <TextField
              fullWidth
              size="small"
              type="date"
              label="F. Vencimiento"
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
        </Grid>

        {/* Segunda fila: Cliente, RUC/DNI, Dirección */}
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              label="Cliente"
              variant="outlined"
              size="small"
              fullWidth
              required
            />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              label="RUC/DNI"
              variant="outlined"
              size="small"
              fullWidth
              required
            />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              label="Dirección"
              variant="outlined"
              size="small"
              fullWidth
            />
          </Grid>
        </Grid>
      </Paper>

      {/* Búsqueda de producto: (Opcional, simulando la UI mostrada) */}
      <Paper variant="outlined" sx={{ p: 2, mb: 3 }}>
        <Grid container spacing={2} alignItems="center">
          <Grid item xs={12} sm={7} md={8}>
            <TextField
              label="Buscar producto por código o descripción"
              variant="outlined"
              size="small"
              fullWidth
            />
          </Grid>
          <Grid item xs={12} sm={5} md={4} sx={{ textAlign: 'right' }}>
            <Button
              variant="contained"
              size="small"
              color="primary"
              onClick={agregarProducto}
              startIcon={<AddIcon />}
            >
              ÍTEM DETALLADO
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {/* Detalle de productos/servicios */}
      <TableContainer component={Paper} sx={{ mb: 2 }}>
        <Table size="small">
          <TableHead>
            <TableRow sx={{ backgroundColor: '#f5f5f5' }}>
              <TableCell align="center">Cant.</TableCell>
              <TableCell>Código</TableCell>
              <TableCell>Descripción</TableCell>
              <TableCell align="right">P.Unit (S/)</TableCell>
              <TableCell align="right">Subtotal (S/)</TableCell>
              <TableCell align="center">Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {productos.map((producto, index) => (
              <TableRow key={index}>
                <TableCell align="center" sx={{ width: 80 }}>
                  <TextField
                    type="number"
                    variant="outlined"
                    size="small"
                    value={producto.cantidad}
                    onChange={(e) =>
                      handleProductoChange(index, 'cantidad', e.target.value)
                    }
                    inputProps={{ min: 0 }}
                  />
                </TableCell>
                {/* Campo "código" (si es necesario, puedes eliminarlo) */}
                <TableCell sx={{ width: 120 }}>
                  <TextField
                    variant="outlined"
                    size="small"
                    placeholder="Código"
                    sx={{ width: '100%' }}
                  />
                </TableCell>
                <TableCell>
                  <TextField
                    variant="outlined"
                    size="small"
                    fullWidth
                    value={producto.descripcion}
                    onChange={(e) =>
                      handleProductoChange(index, 'descripcion', e.target.value)
                    }
                    placeholder="Descripción"
                  />
                </TableCell>
                <TableCell align="right" sx={{ width: 100 }}>
                  <TextField
                    type="number"
                    variant="outlined"
                    size="small"
                    value={producto.precio}
                    onChange={(e) =>
                      handleProductoChange(index, 'precio', e.target.value)
                    }
                    inputProps={{ min: 0 }}
                  />
                </TableCell>
                <TableCell align="right" sx={{ width: 100 }}>
                  {producto.subtotal.toFixed(2)}
                </TableCell>
                <TableCell align="center" sx={{ width: 60 }}>
                  <IconButton color="error" onClick={() => eliminarProducto(index)}>
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Totales */}
      <Grid container spacing={2} justifyContent="flex-end" sx={{ mb: 3 }}>
        <Grid item xs={12} sm={4} md={2}>
          <TextField
            label="Subtotal (S/)"
            variant="outlined"
            size="small"
            fullWidth
            value={totales.subtotal.toFixed(2)}
            InputProps={{ readOnly: true }}
          />
        </Grid>
        <Grid item xs={12} sm={4} md={2}>
          <TextField
            label="IGV (18%)"
            variant="outlined"
            size="small"
            fullWidth
            value={totales.igv.toFixed(2)}
            InputProps={{ readOnly: true }}
          />
        </Grid>
        <Grid item xs={12} sm={4} md={2}>
          <TextField
            label="Total (S/)"
            variant="outlined"
            size="small"
            fullWidth
            value={totales.total.toFixed(2)}
            InputProps={{ readOnly: true }}
          />
        </Grid>
      </Grid>

      {/* Botones de acción */}
      <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 2 }}>
        <Button variant="contained" color="primary" size="small">
          GUARDAR
        </Button>
        <Button variant="outlined" color="secondary" size="small">
          CANCELAR
        </Button>
      </Box>
    </Box>
  );
};

export default Facturacion;
