import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import Divider from '@mui/material/Divider';
import Avatar from '@mui/material/Avatar';
import LogoutIcon from '@mui/icons-material/Logout';
import HomeIcon from '@mui/icons-material/Home';
import InventoryIcon from '@mui/icons-material/Inventory';
import PeopleIcon from '@mui/icons-material/People';
import ReceiptIcon from '@mui/icons-material/Receipt';
import SettingsIcon from '@mui/icons-material/Settings';

const drawerWidth = 80; // Reducimos el ancho del menú para que solo quepan los íconos

const Menu = () => {
  const [selected, setSelected] = useState('Inicio'); // Estado para rastrear la pestaña activa

  const handleSelection = (item: string) => {
    setSelected(item);
  };

  return (
    <Box sx={{ display: 'flex' }}>
      {/* AppBar */}
      <AppBar
        position="fixed"
        sx={{
          zIndex: (theme) => theme.zIndex.drawer + 1,
          backgroundColor: '#1e3a8a', // Azul oscuro
        }}
      >
        <Toolbar>
          <Typography variant="h6" noWrap component="div">
            Facturación y Control de Ventas
          </Typography>
        </Toolbar>
      </AppBar>

      {/* Drawer */}
      <Drawer
        variant="permanent" // Menú estático
        anchor="left"
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
            backgroundColor: '#e5e7eb', // Gris claro
          },
        }}
      >
        <Box sx={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
          <Typography variant="h6" sx={{ p: 2, textAlign: 'center', color: '#1e3a8a', fontSize: '1rem' }}>
            bitepoint
          </Typography>
          <Divider />
          <List>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/inicio"
                onClick={() => handleSelection('Inicio')}
                sx={{
                  backgroundColor: selected === 'Inicio' ? 'rgba(30, 58, 138, 0.2)' : 'transparent',
                  justifyContent: 'center',
                }}
              >
                <ListItemIcon>
                  <HomeIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/productos"
                onClick={() => handleSelection('Productos')}
                sx={{
                  backgroundColor: selected === 'Productos' ? 'rgba(30, 58, 138, 0.2)' : 'transparent',
                  justifyContent: 'center',
                }}
              >
                <ListItemIcon>
                  <InventoryIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/clientes"
                onClick={() => handleSelection('Clientes')}
                sx={{
                  backgroundColor: selected === 'Clientes' ? 'rgba(30, 58, 138, 0.2)' : 'transparent',
                  justifyContent: 'center',
                }}
              >
                <ListItemIcon>
                  <PeopleIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/facturacion"
                onClick={() => handleSelection('Facturación')}
                sx={{
                  backgroundColor: selected === 'Facturación' ? 'rgba(30, 58, 138, 0.2)' : 'transparent',
                  justifyContent: 'center',
                }}
              >
                <ListItemIcon>
                  <ReceiptIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
              </ListItemButton>
            </ListItem>
            <ListItem disablePadding>
              <ListItemButton
                component={Link}
                to="/configuracion"
                onClick={() => handleSelection('Configuración')}
                sx={{
                  backgroundColor: selected === 'Configuración' ? 'rgba(30, 58, 138, 0.2)' : 'transparent',
                  justifyContent: 'center',
                }}
              >
                <ListItemIcon>
                  <SettingsIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
              </ListItemButton>
            </ListItem>
          </List>
          <Divider />
          <Box sx={{ mt: 'auto', p: 1 }}>
            <Box sx={{ display: 'flex', justifyContent: 'center', mb: 1 }}>
              <Avatar sx={{ backgroundColor: '#1e3a8a', width: 40, height: 40, fontSize: '0.8rem' }}>BT</Avatar>
            </Box>
            <ListItem disablePadding>
              <ListItemButton sx={{ justifyContent: 'center' }}>
                <ListItemIcon>
                  <LogoutIcon sx={{ color: '#1e3a8a' }} />
                </ListItemIcon>
              </ListItemButton>
            </ListItem>
          </Box>
        </Box>
      </Drawer>

      {/* Main Content */}
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 2,
          ml: `${drawerWidth}px`, // Espacio reservado para el menú
        }}
      >
        <Toolbar />
        <Typography paragraph>
          Aquí va el contenido principal de tu aplicación.
        </Typography>
      </Box>
    </Box>
  );
};

export default Menu;
