import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  Grid,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';

// Definimos interfaces para tipar los productos y totales
interface Product {
  descripcion: string;
  cantidad: number;
  precio: number;
  subtotal: number;
}

interface Totals {
  subtotal: number;
  igv: number;
  total: number;
}

const Facturacion: React.FC = () => {
  const [productos, setProductos] = useState<Product[]>([
    { descripcion: '', cantidad: 1, precio: 0, subtotal: 0 },
  ]);
  const [totales, setTotales] = useState<Totals>({ subtotal: 0, igv: 0, total: 0 });

  // Se recalculan los totales cada vez que cambian los productos
  useEffect(() => {
    const newSubtotal = productos.reduce((acc, prod) => acc + prod.subtotal, 0);
    const newIgv = newSubtotal * 0.18;
    const newTotal = newSubtotal + newIgv;
    setTotales({ subtotal: newSubtotal, igv: newIgv, total: newTotal });
  }, [productos]);

  const handleProductoChange = (
    index: number,
    field: keyof Product,
    value: string
  ) => {
    const updatedProductos = [...productos];
    const productoActual = updatedProductos[index];

    // Se actualiza el valor segÃºn el campo
    if (field === 'cantidad' || field === 'precio') {
      const numericValue = parseFloat(value) || 0;
      productoActual[field] = numericValue;
    } else if (field === 'descripcion') {
      productoActual[field] = value;
    }

    // Se recalcula el subtotal del producto
    productoActual.subtotal = productoActual.cantidad * productoActual.precio;
    setProductos(updatedProductos);
  };

  const agregarProducto = () => {
    setProductos((prev) => [
      ...prev,
      { descripcion: '', cantidad: 1, precio: 0, subtotal: 0 },
    ]);
  };

  const eliminarProducto = (index: number) => {
    const updatedProductos = productos.filter((_, i) => i !== index);
    setProductos(updatedProductos);
  };

  return (
    <Box sx={{ p: 3 }}>
      <Paper elevation={3} sx={{ p: 4, borderRadius: 2 }}>
        <Typography variant="h5" sx={{ mb: 3, fontWeight: 600, color: '#1e3a8a' }}>
          FacturaciÃ³n ElectrÃ³nica
        </Typography>

        {/* Datos de la factura */}
        <Grid container spacing={2} sx={{ mb: 3 }}>
          <Grid item xs={12} sm={6} md={4}>
            <TextField label="RUC" required size="small" fullWidth />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <TextField label="NÂ° Factura" required size="small" fullWidth />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <TextField
              label="Fecha"
              type="date"
              required
              fullWidth
              size="small"
              InputLabelProps={{ shrink: true }}
            />
          </Grid>
        </Grid>

        {/* Cliente */}
        <Grid container spacing={2} sx={{ mb: 3 }}>
          <Grid item xs={12} sm={6} md={4}>
            <TextField label="Cliente" required size="small" fullWidth />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <TextField label="RUC/DNI" required size="small" fullWidth />
          </Grid>
          <Grid item xs={12} sm={6} md={4}>
            <TextField label="DirecciÃ³n" size="small" fullWidth />
          </Grid>
        </Grid>

        {/* Tabla de productos */}
        <TableContainer component={Paper} variant="outlined" sx={{ mb: 2 }}>
          <Table size="small">
            <TableHead>
              <TableRow sx={{ backgroundColor: '#f9fafb' }}>
                <TableCell>DescripciÃ³n</TableCell>
                <TableCell align="right">Cantidad</TableCell>
                <TableCell align="right">Precio Unitario (S/.)</TableCell>
                <TableCell align="right">Subtotal (S/.)</TableCell>
                <TableCell align="center">Acciones</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {productos.map((producto, index) => (
                <TableRow key={index}>
                  <TableCell>
                    <TextField
                      variant="outlined"
                      size="small"
                      fullWidth
                      value={producto.descripcion}
                      onChange={(e) =>
                        handleProductoChange(index, 'descripcion', e.target.value)
                      }
                    />
                  </TableCell>
                  <TableCell align="right">
                    <TextField
                      type="number"
                      variant="outlined"
                      size="small"
                      value={producto.cantidad}
                      onChange={(e) =>
                        handleProductoChange(index, 'cantidad', e.target.value)
                      }
                      inputProps={{ min: 0 }}
                    />
                  </TableCell>
                  <TableCell align="right">
                    <TextField
                      type="number"
                      variant="outlined"
                      size="small"
                      value={producto.precio}
                      onChange={(e) =>
                        handleProductoChange(index, 'precio', e.target.value)
                      }
                      inputProps={{ min: 0 }}
                    />
                  </TableCell>
                  <TableCell align="right">
                    {producto.subtotal.toFixed(2)}
                  </TableCell>
                  <TableCell align="center">
                    <IconButton color="error" onClick={() => eliminarProducto(index)}>
                      <DeleteIcon />
                    </IconButton>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        <Button
          variant="outlined"
          size="small"
          startIcon={<AddIcon />}
          onClick={agregarProducto}
          sx={{ mb: 3, borderColor: '#1e3a8a', color: '#1e3a8a', fontWeight: 500 }}
        >
          + AGREGAR PRODUCTO
        </Button>

        {/* Totales */}
        <Grid container spacing={2} justifyContent="flex-end" sx={{ mb: 3 }}>
          <Grid item xs={12} sm={4} md={2}>
            <TextField
              label="Subtotal (S/.)"
              value={totales.subtotal.toFixed(2)}
              InputProps={{ readOnly: true }}
              fullWidth
              size="small"
            />
          </Grid>
          <Grid item xs={12} sm={4} md={2}>
            <TextField
              label="IGV (18%)"
              value={totales.igv.toFixed(2)}
              InputProps={{ readOnly: true }}
              fullWidth
              size="small"
            />
          </Grid>
          <Grid item xs={12} sm={4} md={2}>
            <TextField
              label="Total (S/.)"
              value={totales.total.toFixed(2)}
              InputProps={{ readOnly: true }}
              fullWidth
              size="small"
            />
          </Grid>
        </Grid>

        {/* Botones */}
        <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 2 }}>
          <Button variant="contained" color="primary" size="small">
            GUARDAR
          </Button>
          <Button variant="outlined" color="secondary" size="small">
            CANCELAR
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default Facturacion;
