import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material.Delete';

// Definimos interfaces para tipar los productos y totales
interface Product {
  descripcion: string;
  cantidad: number;
  precio: number;
  subtotal: number;
}

interface Totals {
  subtotal: number;
  igv: number;
  total: number;
}

const Facturacion: React.FC = () => {
  const [productos, setProductos] = useState<Product[]>([
    { descripcion: '', cantidad: 1, precio: 0, subtotal: 0 },
  ]);
  const [totales, setTotales] = useState<Totals>({ subtotal: 0, igv: 0, total: 0 });

  // Se recalculan los totales cada vez que cambian los productos
  useEffect(() => {
    const newSubtotal = productos.reduce((acc, prod) => acc + prod.subtotal, 0);
    const newIgv = newSubtotal * 0.18;
    const newTotal = newSubtotal + newIgv;
    setTotales({ subtotal: newSubtotal, igv: newIgv, total: newTotal });
  }, [productos]);

  const handleProductoChange = (
    index: number,
    field: keyof Product,
    value: string | number
  ) => {
    const updatedProductos = [...productos];
    const productoActual = updatedProductos[index];

    // Se actualiza el valor segÃºn el campo
    if (field === 'cantidad' || field === 'precio') {
      const numericValue = parseFloat(value as string) || 0;
      productoActual[field] = numericValue;
    } else if (field === 'descripcion') {
      productoActual[field] = value;
    }

    // Se recalcula el subtotal del producto
    productoActual.subtotal = productoActual.cantidad * productoActual.precio;
    setProductos(updatedProductos);
  };

  const agregarProducto = () => {
    setProductos((prev) => [
      ...prev,
      { descripcion: '', cantidad: 1, precio: 0, subtotal: 0 },
    ]);
  };

  const eliminarProducto = (index: number) => {
    const updatedProductos = productos.filter((_, i) => i !== index);
    setProductos(updatedProductos);
  };

  const limpiarFactura = () => {
    setProductos([{ descripcion: '', cantidad: 1, precio: 0, subtotal: 0 }]);
    setTotales({ subtotal: 0, igv: 0, total: 0 });
  };

  const anularFactura = () => {
    alert('Factura anulada');
  };

  return (
    <Box sx={{ p: 3, backgroundColor: '#f9f9f9', borderRadius: 2 }}>
      {/* Encabezado */}
      <Typography variant="h5" sx={{ mb: 3, color: 'primary.main', fontWeight: 'bold' }}>
        FacturaciÃ³n ElectrÃ³nica
      </Typography>

      {/* InformaciÃ³n de la factura */}
      <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: 2, mb: 3 }}>
        <FormControl fullWidth size="small">
          <InputLabel>Tipo Documento</InputLabel>
          <Select defaultValue="FACTURA">
            <MenuItem value="FACTURA">Factura</MenuItem>
            <MenuItem value="BOLETA">Boleta</MenuItem>
          </Select>
        </FormControl>
        <TextField label="Serie" variant="outlined" size="small" defaultValue="F001" />
        <TextField label="Correlativo" variant="outlined" size="small" defaultValue="0005" />
        <FormControl fullWidth size="small">
          <InputLabel>Moneda</InputLabel>
          <Select defaultValue="Soles">
            <MenuItem value="Soles">Soles</MenuItem>
            <MenuItem value="DÃ³lares">DÃ³lares</MenuItem>
          </Select>
        </FormControl>
        <TextField label="Fecha" type="date" variant="outlined" size="small" InputLabelProps={{ shrink: true }} />
        <TextField label="F. Vencimiento" type="date" variant="outlined" size="small" InputLabelProps={{ shrink: true }} />
      </Box>

      {/* InformaciÃ³n del cliente */}
      <Box sx={{ display: 'grid', gridTemplateColumns: '3fr 1fr', gap: 2, mb: 3 }}>
        <TextField label="Cliente" variant="outlined" size="small" placeholder="Buscar cliente por RUC o razÃ³n social" />
        <Button variant="contained" color="primary" size="small">
          Registrar Cliente
        </Button>
      </Box>

      {/* Producto */}
      <Box sx={{ display: 'grid', gridTemplateColumns: '3fr 1fr', gap: 2, mb: 3 }}>
        <TextField label="Producto" variant="outlined" size="small" placeholder="Buscar producto por cÃ³digo o descripciÃ³n" />
        <Button variant="contained" color="primary" size="small">
          Item Detallado
        </Button>
      </Box>

      {/* Detalle de productos/servicios */}
      <TableContainer component={Paper} sx={{ mb: 3 }}>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell>DescripciÃ³n</TableCell>
              <TableCell align="right">Cantidad</TableCell>
              <TableCell align="right">Precio Unitario (S/.)</TableCell>
              <TableCell align="right">Subtotal (S/.)</TableCell>
              <TableCell align="center">Acciones</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {productos.map((producto, index) => (
              <TableRow key={index}>
                <TableCell>
                  <TextField
                    variant="outlined"
                    size="small"
                    fullWidth
                    value={producto.descripcion}
                    onChange={(e) =>
                      handleProductoChange(index, 'descripcion', e.target.value)
                    }
                  />
                </TableCell>
                <TableCell align="right">
                  <TextField
                    type="number"
                    variant="outlined"
                    size="small"
                    value={producto.cantidad}
                    onChange={(e) =>
                      handleProductoChange(index, 'cantidad', e.target.value)
                    }
                    inputProps={{ min: 0 }}
                  />
                </TableCell>
                <TableCell align="right">
                  <TextField
                    type="number"
                    variant="outlined"
                    size="small"
                    value={producto.precio}
                    onChange={(e) =>
                      handleProductoChange(index, 'precio', e.target.value)
                    }
                    inputProps={{ min: 0 }}
                  />
                </TableCell>
                <TableCell align="right">{producto.subtotal.toFixed(2)}</TableCell>
                <TableCell align="center">
                  <IconButton color="error" onClick={() => eliminarProducto(index)}>
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Button
        variant="outlined"
        size="small"
        startIcon={<AddIcon />}
        onClick={agregarProducto}
        sx={{ mb: 3 }}
      >
        Agregar Producto
      </Button>

      {/* Totales */}
      <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: 2, mb: 3 }}>
        <TextField
          label="Subtotal (S/.)"
          variant="outlined"
          size="small"
          value={totales.subtotal.toFixed(2)}
          disabled
        />
        <TextField
          label="IGV (18%)"
          variant="outlined"
          size="small"
          value={totales.igv.toFixed(2)}
          disabled
        />
        <TextField
          label="Total (S/.)"
          variant="outlined"
          size="small"
          value={totales.total.toFixed(2)}
